// import logo from './logo.svg';
import React from 'react';

import './App.scss';
// import Greet from './components/Greet';
import Nav from './components/Nav/Nav';
import Footer from './components/Footer/Footer';
import Main from './components/Main/Main';

// fix dark mode
// function mySwitch() {
// 	var element2 = document.body;
// 	element2.classList.toggle('dark');
// }

function App() {

	const request = require('request');

	const options = {
	  method: 'GET',
	  url: 'https://mlb-data.p.rapidapi.com/json/named.player_teams.bam',
	  qs: {player_id: '\'493316\'', season: '\'2014\''},
	  headers: {
		'x-rapidapi-host': 'mlb-data.p.rapidapi.com',
		'x-rapidapi-key': 'undefined',
		useQueryString: true
	  }
	};

	request(options, function (error, response, body) {
		if (error) throw new Error(error);

		console.log(body);
	});


	const [darkMode, setDarkMode] = React.useState(false);

	React.useEffect(() => {
		const json = localStorage.getItem('lerko96-dark-mode');
		const currentMode = JSON.parse(json);
		if (currentMode) {
			setDarkMode(true);
		} else {
			setDarkMode(false);
		}
	}, []);

	React.useEffect(() => {
		if (darkMode) {
			document.body.classList.add('dark');
		} else {
			document.body.classList.remove('dark');
		}
		const json = JSON.stringify(darkMode);
		localStorage.setItem('lerko96-dark-mode', json);
	}, [darkMode]);

	return (
		<div className='App'>
			<Nav />
			<button onClick={() => setDarkMode(!darkMode)}>
				Toggle Dark Mode
			</button>
			<Main />
			<Footer />
		</div>
	);
}

export default App;
